import React from 'react';

class GoogleAuth extends React.Component {

    state = {
        isSignedIn: null
    };

    componentDidMount() {
        window.gapi.load('client:auth2', () => {
            window.gapi.client.init({
                clientId: '532968344426-0ql97hf6gpp09m4u414iuroert00f3im.apps.googleusercontent.com',
                scope: 'email'
            }).then(() => {
                this.auth = window.gapi.auth2.getAuthInstance();
                this.setState({
                    isSignedIn: this.auth.isSignedIn.get()
                });
                this.auth.isSignedIn.listen(this.onAuthChange);
            });
        });
    }

    onSignIn = () => {
        this.auth.signIn();
    };

    onSignOut = () => {
        this.auth.signOut();
    };

    renderAuthButton() {
        if(this.state.isSignedIn === null) {
            return null;
        } else if(this.state.isSignedIn) {
            return(
                <button className="ui item google button" onClick={this.onSignOut}>
                    <i className="google plus icon">
                        Sign Out
                    </i>
                </button>
            );
        } else {
            return(
                <button className="ui item google button" onClick={this.onSignIn}>
                    <i className="google plus icon">
                        Sign In with Google
                    </i>
                </button>
            );
        }
    }

    onAuthChange = () => {
        this.setState({isSignedIn:this.auth.isSignedIn.get()});
    };

    render() {
        return(
            <div>
                {this.renderAuthButton()}
            </div>
        );
    }
}

export default GoogleAuth;
